// SPDX-License-Identifier: MIT

pragma solidity 0.6.11;


interface IJLoanTokenDeployer {
    function setLoanFactory(address _factAddr) external;
    function setTokenLoansCounter(uint _newValue) external;
    function getDeployedTokenLoansCounter() external view returns(uint);
    function getDeployedTokenLoanAddress(uint idx) external view returns(address);
    function isTokenLoanDeployed(address _loanAddr) external view returns (bool);
    function deployNewTokenLoanContract(address _sender, 
        uint _id, 
        uint _pairId, 
        uint _borrowedAskAmount, 
        uint _rpbRate, 
        address _factAddr,
        address _collToken,
        address _lendToken,
        uint _contractAmount) external returns (address);
    function checkAllowance(address _sender, address _tokenAddr, address _loanContract, uint _borrowedAskAmount) external view returns (bool);
}
