// SPDX-License-Identifier: MIT

pragma solidity ^0.6.11;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/math/Math.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./IJLoanCommons.sol";
import "./IJLoanEth.sol";
import "./IJFactory.sol";
import "./TransferHelper.sol";

contract JLoanEth is Ownable, ReentrancyGuard, IJLoanCommons {
    using SafeMath for uint;

    address public ethDeployer;
    address payable public feesCollector;

    address public borrowerAddress;

    IERC20 public lentToken;

    params public loanParams;
    contractParams public commonParams;
    Status public loanStatus;
    feesParams public loanFees;

    uint public loanActiveBlock;
    uint public loanInitiateForeCloseBlock;
    uint public loanClosingBlock;
    uint public loanClosedBlock;
    uint public lastDepositBlock;
    uint public lastInterestWithdrawalBlock;
    uint public loanForeclosingBlock;
    uint public loanForeclosedBlock;

    struct ShareHolder {
        address holder;
        uint shares;
        uint ownerBlockNumber;
    }

    mapping(uint => ShareHolder) public shareHolders;
    mapping(address => bool) public shareHoldersAddress;
    mapping(address => uint) public shareHoldersPlace;
    uint public shareHoldersCounter;

    event LoanContractDeployed(uint creationBlock, uint value, string eth);
    event NewDeposit(uint depositAmount);
    event EthReceived(address sender, uint value);
    event EthWithdraw(uint blockNumber);
    event LenderAccepted(address indexed lenderAddress, uint factoryFees, uint loanActiveBlock);
    event InitiateForeClosure(address indexed sender, uint blockNumber);
    event LoanAtRiskFromForeclosing(address indexed sender, uint blockNumber);
    event LoanForeclosing(address indexed sender, uint blockNumber);
    event LoanForeclosed(address indexed sender, uint blockNumber);
    event LoanCancelled(address indexed sender, uint indexed loanId, uint blockNumber);
    event NewLoanStatus(uint loanStatus);
    event NewShareHolder(address indexed sharesSeller, address indexed newShareholder, uint indexed amount, uint blockNumber);
    event AddShareHolderShares(address indexed sharesSeller, address indexed sharesBuyer, uint indexed amount, uint blockNumber);
    event LoanClosingByBorrower(uint blockNumber);
    event LoanClosed(uint blockNumber);
    event InterestsWithdrawed(uint blockNumber);

    //// Status = 0
    constructor(address _sender,
                uint _id, 
                uint _pairId, 
                uint _borrowedAskAmount, 
                uint _rpbRate, 
                address _factoryAddress,
                address _lendCrypto,
                address payable _ethFeesCollector) public payable {  
        require(msg.sender!=address(0), "Sender address not allowed");
        ethDeployer = msg.sender;
        require(_sender!=address(0), "Sender address not allowed");
        borrowerAddress = _sender;
        loanParams = IJFactory(_factoryAddress).getGeneralParams();
        loanFees = IJFactory(_factoryAddress).getGeneralFees();
        uint totalCollateralRequest = IJFactory(_factoryAddress).calcMinCollateralWithFeesAmount(_pairId, _borrowedAskAmount);
        require(borrowerAddress.balance >= totalCollateralRequest, "Borrower does not have enough ethers");
        require(msg.value >= totalCollateralRequest, "Please send more ethers");
        feesCollector = _ethFeesCollector;
        require(_rpbRate >= loanParams.minRpbRate, "Interest too low!");
        commonParams.loanId = _id;
        commonParams.rpbRate = _rpbRate;
        commonParams.pairId = _pairId;
        lentToken = IERC20(_lendCrypto); //DAI
        commonParams.borrowedAskAmount = _borrowedAskAmount;
        commonParams.collDecimals = 18; //eth decimals
        commonParams.lendDecimals = IJFactory(_factoryAddress).getPairQuoteDecimals(_pairId); //lend token decimals
        commonParams.initialCollateralRatio = getActualCollateralRatio();
        commonParams.creationBlock = block.number;
        loanStatus = Status.pending;
        //Transfer ownership to factory address
        transferOwnership(loanParams.factoryAddress);
        emit LoanContractDeployed(commonParams.creationBlock, msg.value, "eth");
    }

    modifier onlyEthDeployer() {
        require(msg.sender == ethDeployer, "Caller is not a valid deployer!");
        _;
    }

    fallback() external payable {
        require(loanStatus >= Status.active && loanStatus <= Status.foreClosing, "Loan status cannot receive collateral");
        emit EthReceived(msg.sender, msg.value);
    }

    receive() external payable { //deposit eth
        require(loanStatus >= Status.active && loanStatus <= Status.foreClosing, "Loan status cannot receive collateral");
        emit EthReceived(msg.sender, msg.value);
    }

    //// Utility functions
    /**
    * @dev set common parameters in loan contract, reading them from factory
    */
    function setNewCommonParams() external onlyEthDeployer {
        loanParams = IJFactory(loanParams.factoryAddress).getGeneralParams();
    }

    /**
    * @dev set common parameters in loan contract, reading them from factory
    */
    function setNewFeesParams() external onlyEthDeployer {
        loanFees = IJFactory(loanParams.factoryAddress).getGeneralFees();
    }

    /**
    * @dev withdraw ethers from contract, every shareholder with his percentage
    */
    /*function withdrawEth() public nonReentrant {
        require(loanStatus >= Status.closed, "Loan status not compatible with foreclosing actions" );
        require(isShareHolder(msg.sender), "Address requiring interest withdraw not allow");
        for (uint8 i = 1; i <= shareHoldersCounter; i++) {
            address shAddress = shareHolders[i].holder;
            uint shPlace = getShareHolderPlace(shareHolders[i].holder);
            uint shShares = shareHolders[shPlace].shares;
            uint withdrawAmount = address(this).balance.mul(shShares).div(100);
            shareHolders[shPlace].shares = 0;
            TransferHelper.safeTransferETH(shAddress, withdrawAmount);
        }
        emit EthWithdraw(block.number);
    }*/

    /**
    * @dev get contract balance
    * @return balance
    */
    function getContractBalance() public view returns (uint) {
        return address(this).balance;
    }

    /**
    * @dev get contract status
    * @return status
    */
    function getLoanStatus() external view returns (uint){
        return uint(loanStatus);
    }

    /**
    * @dev get pair price
    * @return price
    */
    function getPairPrice() public view returns (uint){
        return IJFactory(loanParams.factoryAddress).getPairValue(commonParams.pairId);
    }

    /**
    * @dev get pair decimals
    * @return number of decimals
    */
    function getPairDecimals() public view returns (uint){
        return IJFactory(loanParams.factoryAddress).getPairDecimals(commonParams.pairId);
    }

    /**
    * @dev check if loan is in early settlement period
    * @return boolean
    */
    function checkEarlySettledLoan() public view returns (bool) {
        return loanStatus == Status.earlyClosing;
    }

    /**
    * @dev get the collateral ratio of the loan (subtracting the accrued interests)
    * @return newCollRatio collateral ratio
    */
    function getActualCollateralRatio() public view returns (uint newCollRatio) {
        uint newPrice = getPairPrice();
        uint pairDecimals = getPairDecimals();
        uint borrAmnt = commonParams.borrowedAskAmount;
        uint loanBalance;
        if (loanStatus == Status.pending)
            loanBalance = address(this).balance;
        else
            loanBalance = (address(this).balance).sub(getAccruedInterests());
        if (commonParams.collDecimals >= commonParams.lendDecimals) {
            uint diffBaseQuoteDecimals = uint(commonParams.collDecimals).sub(uint(commonParams.lendDecimals));
            newCollRatio = loanBalance.mul(newPrice).div(borrAmnt).mul(100).div(10 ** pairDecimals).div(10 ** diffBaseQuoteDecimals);
        } else {
            uint diffBaseQuoteDecimals = uint(commonParams.lendDecimals).sub(uint(commonParams.collDecimals));
            newCollRatio = loanBalance.mul(newPrice).div(borrAmnt).mul(100).div(10 ** pairDecimals).mul(10 ** diffBaseQuoteDecimals);
        }
        return newCollRatio;
    }

    /**
    * @dev calc a new ratio if collateral amount has added to contract balance
    * @param _amount collateral amount to add
    * @return ratio new collateral ratio, percentage with no decimals
    */
    function calcRatioAddingCollateral(uint _amount) external view returns (uint ratio) {
        uint bal = address(this).balance;
        uint newBal = bal.add(_amount);
        uint actualPrice = getPairPrice();
        uint pairDecimals = getPairDecimals();
        uint borrAmnt = commonParams.borrowedAskAmount;
        if (commonParams.collDecimals >= commonParams.lendDecimals) {
            uint diffBaseQuoteDecimals = uint(commonParams.collDecimals).sub(uint(commonParams.lendDecimals));
            ratio = newBal.mul(actualPrice).div(borrAmnt).mul(100).div(10 ** pairDecimals).div(10 ** diffBaseQuoteDecimals);
        } else {
            uint diffBaseQuoteDecimals = uint(commonParams.lendDecimals).sub(uint(commonParams.collDecimals));
            ratio = newBal.mul(actualPrice).div(borrAmnt).mul(100).div(10 ** pairDecimals).mul(10 ** diffBaseQuoteDecimals);
        }
        return ratio;
    }

    /**
    * @dev calc how much collateral amount has to be added to have a ratio
    * @param _ratio ratio to reach, percentage with no decimals (180 means 180%)
    * @return collDiff collateral amount to add or to subtract to reach that ratio
    */
    function calcDiffCollAmountForRatio(uint _ratio) external view returns (uint collDiff) {
        uint bal = address(this).balance;
        uint actualPrice = getPairPrice();
        uint pairDecimals = getPairDecimals();
        uint newBal;
        uint newRatio = _ratio;
        uint borrAmnt = commonParams.borrowedAskAmount;
        if (commonParams.collDecimals >= commonParams.lendDecimals) {
            uint diffBaseQuoteDecimals = uint(commonParams.collDecimals).sub(uint(commonParams.lendDecimals));
            newBal = borrAmnt.mul(newRatio).mul(10 ** pairDecimals).div(actualPrice).div(100).mul(10 ** diffBaseQuoteDecimals);
        } else {
            uint diffBaseQuoteDecimals = uint(commonParams.lendDecimals).sub(uint(commonParams.collDecimals));
            newBal = borrAmnt.mul(newRatio).mul(10 ** pairDecimals).div(actualPrice).div(100).div(10 ** diffBaseQuoteDecimals);
        }
        if (newBal >= bal)
            collDiff = newBal.sub(bal);
        else
            collDiff = bal.sub(newBal);
        return collDiff;
    }

    //// Status 0 -> 1
    /**
    * @dev lender sends required stable coins to borrower and set the initial lender as a stakeholder (100%)
    * @param _stableAddr, address of the stabl coin address
    */
    function lenderSendStableCoins(address _stableAddr) external nonReentrant {
        require(msg.sender != address(0), "Address not allowed");
        require(_stableAddr == address(lentToken), "Token not allowed");
        require(lentToken.balanceOf(msg.sender) >= commonParams.borrowedAskAmount, "Not enough tokens on lender balance");
        require(loanStatus == Status.pending, "Incorrect status");
        uint askAmount = commonParams.borrowedAskAmount;
        uint256 allowance = lentToken.allowance(msg.sender, address(this));
        require(allowance >= askAmount, "Check the token allowance");
        shareHoldersCounter = 1;
        shareHolders[shareHoldersCounter] = ShareHolder({holder: msg.sender, shares: 100, ownerBlockNumber: loanActiveBlock});
        shareHoldersAddress[msg.sender] = true;
        shareHoldersPlace[msg.sender] = shareHoldersCounter;
        loanStatus = Status.active;
        loanActiveBlock = block.number;
        lastInterestWithdrawalBlock = block.number;
        TransferHelper.safeTransferFrom(address(lentToken), msg.sender, borrowerAddress, askAmount);
        // move factory fees only when loan becomes active
        uint minCollateralEth = IJFactory(loanParams.factoryAddress).calcMinCollateralAmount(commonParams.pairId, commonParams.borrowedAskAmount);
        uint fees4Factory = IJFactory(loanParams.factoryAddress).calculateCollFeesOnActivation(minCollateralEth); // 5/1000 = 0,5%
        TransferHelper.safeTransferETH(payable(feesCollector), fees4Factory);
        emit LenderAccepted(msg.sender, fees4Factory, loanActiveBlock);
    }

    //// Status 1 or 2 or 3, based on collateral ratio
    /**
    * @dev set the status of the loan based on collateral ratio, applied only in states allowed 
    * @return loan status
    */
    function setLoanStatusOnCollRatio() public returns (uint){
        if (uint(loanStatus) > 0 && uint(loanStatus) < 5) {     //not allowed if loan not active or foreclosed or in closing status or closed
            uint newCollRatio = getActualCollateralRatio(); // (i.e. 180 means 180%)
            if ( newCollRatio >= loanParams.foreclosingRatio ) {
                loanStatus = Status(1);
                loanForeclosingBlock = 0;   //reset foreclosing block
            } else if ( newCollRatio <= loanParams.foreclosingRatio && newCollRatio >= loanParams.instantForeclosureRatio ) {
                loanStatus = Status(2);
            } else if ( newCollRatio < loanParams.instantForeclosureRatio ) {
                loanStatus = Status(3);
            }  
        }
        // else no change on status
        emit NewLoanStatus(uint(loanStatus));
        return uint(loanStatus);
    }

    //// Status 4 or 5, starting from 2 or 3, depending on collateral ratio
    /**
    * @dev set the loan in foreclosure state for undercollateralized loans (tbd: reward for caller)
    */
    function initiateLoanForeClose() external {
        uint status = setLoanStatusOnCollRatio();
        require(status == 2 || status == 3, "Loan status not compatible with foreclosing actions");
        uint reward;
        if (status == 2) {
            reward = (loanFees.undercollateralizedForeclosingMultiple).mul(commonParams.rpbRate);
            setLoanForeclosing();
        } else {
            reward = (loanFees.atRiskForeclosedMultiple).mul(commonParams.rpbRate);
            setLoanForeclosed();
        }
        require(address(this).balance >= reward, "Not enough tokens in loan contract");
        TransferHelper.safeTransferETH(msg.sender, reward.mul(loanFees.userRewardShare).div(100));
        TransferHelper.safeTransferETH(payable(feesCollector), reward.mul(loanFees.vaultShare).div(100));
        loanInitiateForeCloseBlock = block.number;
        emit InitiateForeClosure(msg.sender, loanInitiateForeCloseBlock);
    }

    //// Status 2 -> 4
    /**
    * @dev set the loan in foreclosure state for undercollateralized loans
    */
    function setLoanForeclosing() internal {
        loanStatus = Status.foreClosing;
        // get the block to calculate time to set it in foreclosed if no actions from the borrower
        loanForeclosingBlock = block.number;
        emit LoanForeclosing(msg.sender, block.number);
    }

    /**
    * @dev set the loan in foreclosed state when foreclosureWindow time passed
    */
    function setLoanInForeclosedByTime() external returns (bool) {
        require(uint(loanStatus) == 4, "Loan Status incorrect");
        bool result = false;
        if (loanForeclosingBlock != 0 && block.number >= loanForeclosingBlock.add(loanParams.foreclosureWindow)) {
            setLoanForeclosed();
            result = true;
        }
        return result;
    }

    //// Status 2 -> 5
    /**
    * @dev set the loan in foreclosure state for undercollateralized loans
    */
    function setLoanForeclosed() internal {
        /*uint interestsAmount = getAccruedInterests();
        for (uint8 i = 1; i <= shareHoldersCounter; i++) {
            uint shPlace = getShareHolderPlace(shareHolders[i].holder);
            uint shShares = shareHolders[shPlace].shares;
            TransferHelper.safeTransferETH(payable(shareHolders[i].holder), interestsAmount.mul(shShares).div(100));
        }*/
        loanForeclosedBlock = block.number;
        loanStatus = Status.foreClosed;
        emit LoanForeclosed(msg.sender, block.number);
    }

    //// Status = 6
    function loanEarlyClosing() internal returns (uint) {
        require(block.number < loanActiveBlock + loanParams.earlySettlementWindow, "Incongruent status");
        loanStatus = Status.earlyClosing;
        uint balanceRequested = (loanParams.earlySettlementWindow).mul(commonParams.rpbRate);
        return balanceRequested;
    }

    //// Status = 7
    /**
    * @dev settle the loan in normal closing state by borrower
    */
    function loanClosingByBorrower() external nonReentrant {
        require(msg.sender == borrowerAddress, "Caller not the borrower");
        uint status = setLoanStatusOnCollRatio();
        require(status > 0 && status <= 4, "Loan status not compatible with closing actions");
         // check that borrower gives back the loaned stable coin amount
        uint256 allowance = lentToken.allowance(msg.sender, address(this));
        require(allowance >= commonParams.borrowedAskAmount, "Check the token allowance to settle the loan");
        //TransferHelper.safeTransferFrom(address(lentToken), msg.sender, address(this), commonParams.borrowedAskAmount);
        for (uint8 i = 1; i <= shareHoldersCounter; i++) {
            address shAddress = shareHolders[i].holder;
            uint shPlace = getShareHolderPlace(shareHolders[i].holder);
            uint shShares = shareHolders[shPlace].shares;
            //shareHolders[shPlace].shares = 0;
            //shareHoldersAddress[shareHolders[i].holder] = false;
            TransferHelper.safeTransferFrom(address(lentToken), msg.sender, shAddress, (commonParams.borrowedAskAmount).mul(shShares).div(100));
        }
        uint balanceRequested;
        if (block.number < loanActiveBlock + loanParams.earlySettlementWindow)
            balanceRequested = loanEarlyClosing();
        else
            balanceRequested = getAccruedInterests();
        uint feesAmount = 0;
        if (loanStatus == Status.earlyClosing) 
            feesAmount = (loanFees.earlySettlementFee).mul(commonParams.rpbRate);
        // check if there are enough collateral
        uint bal = address(this).balance;
        uint requestedAmount = balanceRequested.add(feesAmount);
        if (bal >= requestedAmount) {
            TransferHelper.safeTransferETH(payable(feesCollector), feesAmount);
            // borrower receive back collateral
            uint withdrawalBalance = bal.sub(requestedAmount); 
            TransferHelper.safeTransferETH(msg.sender, withdrawalBalance);
        } else if (bal >= feesAmount) {
            TransferHelper.safeTransferETH(payable(feesCollector), feesAmount);
            // borrower doesnt receive any collateral, what reamins it is ditributed to lender(s) as the RPB
        }
        loanClosingBlock = block.number;
        loanStatus = Status.closing;
        emit LoanClosingByBorrower(loanClosingBlock);
    }


    //// Status = 8
    /**
    * @dev set the loan in closed state, let Shareholders to withdraw the stable coins back
    */
    function setLoanClosed() internal {
        require(isShareHolder(msg.sender), "Not a shareholder");
        require(uint(loanStatus) >= 6, "Loan status not compatible with closed actions");
        loanClosedBlock = block.number;
        loanStatus = Status.closed;
        emit LoanClosed(loanClosedBlock);
    }

    //// Status = 9
    /**
    * @dev set the loan in cancelled state (only if pending)
    */
    function setLoanCancelled() external {
        require(msg.sender == borrowerAddress, "Cancel request not allowed");
        require(uint(loanStatus) == 0, "Loan status not compatible with cancel actions");
        uint feeCanc = (address(this).balance).mul(loanFees.cancellationFees).div(100);
        TransferHelper.safeTransferETH(payable(feesCollector), feeCanc);
        uint withdrawalBalance = (address(this).balance).sub(feeCanc);
        TransferHelper.safeTransferETH(msg.sender, withdrawalBalance);
        loanStatus = Status.cancelled;
        emit LoanCancelled(msg.sender, commonParams.loanId, block.number);
    }


    //// Calculating interests functions
    /**
    * @dev calculate accrued interests of the contract
    * @return accrued interests
    */
    function calculatingAccruedInterests(uint _calcBlk) public view returns (uint) {
        require(_calcBlk >= lastInterestWithdrawalBlock, "Block number not allowed");
        return _calcBlk.sub(lastInterestWithdrawalBlock).mul(commonParams.rpbRate);  
    }

    /**
    * @dev get accrued interests of the contract
    * @return accruedInterests total accrued interests
    */
    function getAccruedInterests() public view returns (uint accruedInterests) {
        require(uint(loanStatus) > 0 && uint(loanStatus) < 8, "Loan status not calculating interests");
        //require(isShareHolder(msg.sender), "Caller not allowed");
        uint lastEarlyBlock = loanActiveBlock.add(loanParams.earlySettlementWindow);
        if (loanStatus == Status.earlyClosing && block.number <= lastEarlyBlock) {
            accruedInterests = calculatingAccruedInterests(lastEarlyBlock);
        } else {
             if (loanClosedBlock != 0)
                accruedInterests = calculatingAccruedInterests(Math.min(loanClosedBlock, block.number));
            else
                accruedInterests = calculatingAccruedInterests(block.number);
        }
        if ( accruedInterests > address(this).balance )
            accruedInterests = address(this).balance;
        return accruedInterests;
    }

    /**
    * @dev withdraw accreud interests for all shareholders and set the status after interests withdrawal
    * @return new status
    */
    function withdrawInterests() public nonReentrant returns (uint) {
        // interests of all shareholders must be withdrawn with their shares amount
        require(uint(loanStatus) > 0 && uint(loanStatus) < 8, "Loan status not compatible with withdraw accrued interests actions" );
        uint status = uint(loanStatus);
        uint bal = address(this).balance;
        uint accruedTotalInterests = getAccruedInterests();
        if (bal >= accruedTotalInterests) {
            for (uint8 i = 1; i <= shareHoldersCounter; i++) {
                shareholderWithdrawInterests(shareHolders[i].holder, accruedTotalInterests);
            }
            lastInterestWithdrawalBlock = block.number;
            if (status <= 3)
                status = setLoanStatusOnCollRatio();
        } else if (bal == 0) {
            setLoanClosed();
        } else {
            for (uint8 i = 1; i <= shareHoldersCounter; i++) {
                shareholderWithdrawInterests(shareHolders[i].holder, bal);
            }
            lastInterestWithdrawalBlock = block.number;
            loanClosedBlock = block.number;
            loanStatus = Status.closed;
            status = uint(loanStatus);
        }
        emit InterestsWithdrawed(block.number);
        return status;
    }

    //// Shareholders functions
    /**
    * @dev withdraw accreud interests for a shareholder
    * @param _shareHolder shareholder address
    */
    function shareholderWithdrawInterests(address _shareHolder, uint _accruedTotalInterests) internal {
        require(isShareHolder(_shareHolder), "Address is not a shareholder"); 
        for (uint8 i = 1; i <= shareHoldersCounter; i++) {
            uint shPlace = getShareHolderPlace(shareHolders[i].holder);
            uint shShares = shareHolders[shPlace].shares;
            TransferHelper.safeTransferETH(payable(_shareHolder), _accruedTotalInterests.mul(shShares).div(100));
        }
    }

    /**
    * @dev check if an address is a shareholder
    * @return boolean
    */
    function isShareHolder(address _holder) public view returns (bool) {
        return shareHoldersAddress[_holder];
    }

    /**
    * @dev get a shareholder place in shareholders array
    * @return boolean
    */
    function getShareHolderPlace(address _holder) public view returns (uint) {
        return shareHoldersPlace[_holder];
    }

    /**
    * @dev add shareholder in shareholders arrays
    * @param _newShareholder buyer address
    * @param _amount amount of shares
    * @return new status
    */
    function addLoanShareHolders(address _newShareholder, uint _amount) public nonReentrant returns (uint) {
        require(isShareHolder(msg.sender), "Address is not a shareholder");
        uint shPlace = getShareHolderPlace(msg.sender);
        require(shPlace > 0, "Shareholder not found");
        require(shareHolders[shPlace].shares >= _amount, "Shareholder has not enough shares to sell");
        //before shares could be selled, interests of all shareholders must be withdrawn with old shares amount, in order to start with a new situation
        require(block.number > loanActiveBlock + loanParams.earlySettlementWindow, "Too short period after loan activation");
        uint interestsAmount = getAccruedInterests();
        for (uint8 i = 1; i <= shareHoldersCounter; i++) {
            shareholderWithdrawInterests(shareHolders[i].holder, interestsAmount);
        }

        shareHolders[shPlace].shares = shareHolders[shPlace].shares.sub(_amount);
        if(!isShareHolder(_newShareholder)) {
            shareHoldersCounter = shareHoldersCounter.add(1);
            shareHolders[shareHoldersCounter] = ShareHolder({holder: _newShareholder, shares: _amount, ownerBlockNumber: block.number});
            shareHoldersAddress[_newShareholder] = true;
            shareHoldersPlace[_newShareholder] = shareHoldersCounter;
        } else {
            uint shPlaceNew = getShareHolderPlace(_newShareholder);
            shareHolders[shPlaceNew].shares = shareHolders[shPlaceNew].shares.add(_amount);
            shareHolders[shPlaceNew].ownerBlockNumber = block.number;
            emit AddShareHolderShares(msg.sender, _newShareholder, _amount, block.number);
        }

        lastInterestWithdrawalBlock = block.number;
        uint status = setLoanStatusOnCollRatio();
        return status;
    }

}

