// SPDX-License-Identifier: MIT

pragma solidity 0.6.11;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
//import "./Ownable.sol";
import "./IJFactory.sol";
import "./IJLoanCommons.sol";
import "./JLoanEth.sol";


contract JLoanEthDeployer is Ownable {
    using SafeMath for uint256;

    uint private ethLoansCounter;
    address payable private ethFeesCollector;

    mapping(uint => address) private ethLoanContractsList;
    mapping(address => bool) private ethLoanContracts;

    uint public borrowerLength;
    address[] public borrowerList;
    mapping(address => bool) public borrowers;

    IJLoanCommons.GeneralParams public loanParams;

    event SetNewStatus(uint idx, uint oldStatus, uint newStatus);

    constructor() public { }

    modifier onlyLoanFactory() {
        require(msg.sender == loanParams.factoryAddress, "!factory");
        _;
    }

    /**
    * @dev set loan factory contract address
    * @param _factAddr, address of loan factory contract to add
    */
    function setLoanFactory(address _factAddr) external onlyOwner {
        loanParams.factoryAddress = _factAddr;
    }

    /**
    * @dev set eth loans contract counter
    * @param _newValue, new counter value
    */
    function setEthLoansCounter(uint _newValue) external onlyOwner {
        ethLoansCounter = _newValue;
    }

    /**
    * @dev get deployed eth loans contract counter
    * @return number of deployed eth loans contracts
    */
    function getDeployedEthLoansCounter() external view returns(uint) {
        return ethLoansCounter;
    }

    /**
    * @dev get deployed eth loan contract address as an item of an array
    * @return address of idx-th fund contract address
    */
    function getDeployedEthLoanAddress(uint idx) external view returns(address) {
        return ethLoanContractsList[idx];
    }

    /**
    * @dev check if an fund contract address was deployed by this deployer
    * @param _loanAddr, address to check
    * @return true if fund address was deployed, otherwise false
    */
    function isEthLoanDeployed(address _loanAddr) external view returns (bool) {
        return ethLoanContracts[_loanAddr];
    }

     /**
    * @dev deploy a new eth loan contract, add its address to internal variables
    * @param _sender, address of the borrower
    * @param _id, nunmber of the eth loan contract
    * @param _pairId, name of the crypto the borrower would like to receive
    * @param _borrowedAskAmount, amount of crypto the borrower would like to receive
    * @param _rpbRate, rate per block the borrower would like to pay as interest
    * @param _factAddr, factory address
    * @param _lendToken, lender token address
    */              
    function deployNewEthLoanContract(address _sender, 
                uint _id,
                uint _pairId, 
                uint _borrowedAskAmount, 
                uint _rpbRate, 
                address _factAddr,
                address _lendToken) external payable onlyLoanFactory returns (address) {
        require(_sender != address(0), "Sender Address is zero");
        borrowers[_sender] = true;
        borrowerList.push(_sender);
        borrowerLength = borrowerList.length;
        address newEthLoanContract = address(new JLoanEth{value: msg.value}
                    (_sender, _id, _pairId, _borrowedAskAmount, _rpbRate, _factAddr, _lendToken, ethFeesCollector));
        ethLoanContracts[newEthLoanContract] = true;
        ethLoanContractsList[ethLoansCounter] = newEthLoanContract;
        ethLoansCounter = ethLoansCounter.add(1);
        return newEthLoanContract;
    }

    /**
    * @dev set new common GeneralParams in single eth loans contract
    * @param _idx, index of the loan contract
    */
    function setEthLoanCommonParamsInContract (uint _idx) external onlyOwner {
        IJLoanEth(ethLoanContractsList[_idx]).setNewCommonParams();
    }

    /**
    * @dev set new fees GeneralParams in single eth loans contract
    * @param _idx, index of the loan contract
    */
    function setEthLoanFeesParamsInContract (uint _idx) external onlyOwner {
        IJLoanEth(ethLoanContractsList[_idx]).setNewFeesParams();
    }

    /**
    * @dev set new status in single eth loans contract
    * @param _idx, index of the loan contract
    * @param _newStatus, new status
    */
    function setEthStatusInEthContract (uint _idx, uint _newStatus) external onlyOwner {
        require(_newStatus <= 7, "!Status07");
        IJLoanEth ethContract = IJLoanEth(ethLoanContractsList[_idx]);
        uint oldStatus = ethContract.getLoanStatus();
        require(_newStatus != oldStatus, "!newStatus");
        ethContract.setNewStatus(_newStatus);
        uint newStatus = ethContract.getLoanStatus();
        emit SetNewStatus(_idx, oldStatus, newStatus);
    }

    /**
    * @dev get if an address is a borrower
    * @return boolean
    */
    function isBorrower(address _borr) external view returns (bool) {
        return borrowers[_borr];
    }

    /**
    * @dev set the fee collector contract address
    */
    function setFeesCollector(address payable _feeColl) external onlyOwner {
        ethFeesCollector = _feeColl;
    }

    /**
    * @dev get the fee collector contract address
    * @return fee collector address
    */
    function getFeesCollector() external view returns(address payable) {
        return ethFeesCollector;
    }

    /**
    * @dev check for allowance
    * @param _sender, address of the sender
    * @param _tokenAddr, address of the stable coin token
    * @param _loanContract, address of the loan contract
    * @param _borrowedAskAmount, amount of stable coins request by the borrower
    * @return bool
    */
    function checkAllowance(address _sender, address _tokenAddr, address _loanContract, uint _borrowedAskAmount) external view returns (bool) {
        uint askAmount = _borrowedAskAmount;
        uint allowance = IERC20(_tokenAddr).allowance(_sender, _loanContract);
        if (allowance >= askAmount)
            return true;
        else
            return false;
    }

}

