// SPDX-License-Identifier: MIT

pragma solidity 0.6.11;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
//import "./Ownable.sol";
import "./IJFactory.sol";
import "./IJLoanCommons.sol";
import "./JLoanToken.sol";
import "./TransferHelper.sol";


contract JLoanTokenDeployer is Ownable {
    using SafeMath for uint256;

    uint private tokenLoansCounter;
    address private tokenFeesCollector;

    mapping(uint => address) private tokenLoanContractsList;
    mapping(address => bool) private tokenLoanContracts;

    uint public borrowerLength;
    address[] public borrowerList;
    mapping(address => bool) public borrowers;

    IJLoanCommons.GeneralParams public loanParams;

    event SetNewStatus(uint idx, uint oldStatus, uint newStatus);

    constructor() public { }

    modifier onlyLoanFactory() {
        require(msg.sender == loanParams.factoryAddress, "!factory");
        _;
    }

    /**
    * @dev set loan factory contract address
    * @param _factAddr, address of loan factory contract to add
    */
    function setLoanFactory(address _factAddr) external onlyOwner {
        loanParams.factoryAddress = _factAddr;
    }

    /**
    * @dev set token loans contract counter
    * @param _newValue, new counter value
    */
    function setTokenLoansCounter(uint _newValue) external onlyOwner {
        tokenLoansCounter = _newValue;
    }

    /**
    * @dev get deployed token loans contract counter
    * @return number of deployed token loans contracts
    */
    function getDeployedTokenLoansCounter() external view returns(uint) {
        return tokenLoansCounter;
    }

    /**
    * @dev get deployed token loan contract address as an item of an array
    * @return address of idx-th fund contract address
    */
    function getDeployedTokenLoanAddress(uint idx) external view returns(address) {
        return tokenLoanContractsList[idx];
    }

    /**
    * @dev check if an fund contract address was deployed by this deployer
    * @param _loanAddr, address to check
    * @return true if fund address was deployed, otherwise false
    */
    function isTokenLoanDeployed(address _loanAddr) external view returns (bool) {
        return tokenLoanContracts[_loanAddr];
    }

     /**
    * @dev deploy a new token loan contract, add its address to internal variables
    * @param _sender, address of the borrower
    * @param _id, number of the token loan contract
    * @param _pairId, name of the crypto the borrower would like to receive
    * @param _borrowedAskAmount, amount of crypto the borrower would like to receive
    * @param _rpbRate, rate per block the borrower would like to pay as interest
    * @param _factAddr, factory address
    * @param _collToken, collateral token address
    * @param _lendToken, lending token address
    */      //msg.sender, loanID, _pairId, _borrowedAskAmount, _rpbRate, address(this), _collToken, _lendToken)
    function deployNewTokenLoanContract(address _sender, 
                uint _id, 
                uint _pairId, 
                uint _borrowedAskAmount, 
                uint _rpbRate, 
                address _factAddr,
                address _collToken,
                address _lendToken,
                uint _contractAmount) external onlyLoanFactory returns (address) {
        require(_sender != address(0), "ZeroAddress");
        borrowers[_sender] = true;
        borrowerList.push(_sender);
        borrowerLength = borrowerList.length;
        address newTokenLoanContract = address(new JLoanToken(_sender, _id, _pairId, _borrowedAskAmount,
                            _rpbRate, _factAddr, _collToken, _lendToken, tokenFeesCollector));
        tokenLoanContracts[newTokenLoanContract] = true;
        tokenLoanContractsList[tokenLoansCounter] = newTokenLoanContract;
        tokenLoansCounter = tokenLoansCounter.add(1);
        TransferHelper.safeTransferFrom(_collToken, _sender, newTokenLoanContract, _contractAmount);
        return newTokenLoanContract;
    }

    /**
    * @dev set new common GeneralParams in single token loans contract
    * @param _idx, index of the loan contract
    */
    function setTokenLoanCommonParamsInContract (uint _idx) external onlyOwner {
        IJLoanToken(tokenLoanContractsList[_idx]).setNewCommonParams();
    }

    /**
    * @dev set new common GeneralParams in single token loans contract
    * @param _idx, index of the loan contract
    */
    function setTokenLoanFeesParamsInContract (uint _idx) external onlyOwner {
        IJLoanToken(tokenLoanContractsList[_idx]).setNewFeesParams();
    }

    /**
    * @dev set new status in single token loans contract
    * @param _idx, index of the loan contract
    * @param _newStatus, new status
    */
    function setTokenStatusInTokenContract (uint _idx, uint _newStatus) external onlyOwner {
        require(_newStatus <= 7, "!Status07");
        IJLoanToken tokenContract = IJLoanToken(tokenLoanContractsList[_idx]);
        uint oldStatus = tokenContract.getLoanStatus();
        require(_newStatus != oldStatus, "!newStatus");
        tokenContract.setNewStatus(_newStatus);
        uint newStatus = tokenContract.getLoanStatus();
        emit SetNewStatus(_idx, oldStatus, newStatus);
    }

    /**
    * @dev get if an address is a borrower
    * @return boolean
    */
    function isBorrower(address _borr) external view returns (bool) {
        return borrowers[_borr];
    }

    /**
    * @dev set the fee collector contract address
    */
    function setFeesCollector(address payable _feeColl) external onlyOwner {
        tokenFeesCollector = _feeColl;
    }

    /**
    * @dev get the fee collector contract address
    * @return fee collector address
    */
    function getFeesCollector() external view returns(address) {
        return tokenFeesCollector;
    }

    /**
    * @dev check for allowance
    * @param _sender, address of the sender
    * @param _tokenAddr, address of the stable coin token
    * @param _loanContract, address of the loan contract
    * @param _borrowedAskAmount, amount of stable coins request by the borrower
    * @return bool
    */
    function checkAllowance(address _sender, address _tokenAddr, address _loanContract, uint _borrowedAskAmount) external view returns (bool) {
        uint askAmount = _borrowedAskAmount;
        uint allowance = IERC20(_tokenAddr).allowance(_sender, _loanContract);
        if (allowance >= askAmount)
            return true;
        else
            return false;
    }

}

